SELECT  b.TITLE ,b.BOARD_ID,r.REPLY_ID, r.WRITER_ID, r.CONTENTS , DATE_FORMAT(r.CREATED_DATE,'%Y-%m-%d') AS CREATED_AT from
USED_GOODS_REPLY r JOIN USED_GOODS_BOARD b ON b.BOARD_ID = r.BOARD_ID WHERE b.CREATED_DATE >= '2022-10-01' AND  b.CREATED_DATE <= '2022-10-31' ORDER BY r.CREATED_DATE ASC, b.TITLE ASC